'use strict';

var app = angular.module('sbAdminApp');

app.controller('CtrctCtrl', function ($scope) {
	var init = function() {
		$scope.selectChart('Area');
	};
	
	$scope.selectChart = function(arg) {
		if (arg === 'Area') {
			$scope.enableAreaChart = "true";
			$scope.enablePieChart = "false";
			$scope.enableStackedBarChart = "false";
			$scope.enableRadarChart = "false";
			$scope.enableLineChart = "false";
			$scope.enableStepLineChart = "false";
			$scope.enableBarChart = "false";
		} else if (arg === 'Pie') {
			$scope.enableAreaChart = "false";
			$scope.enablePieChart = "true";
			$scope.enableStackedBarChart = "false";
			$scope.enableRadarChart = "false";
			$scope.enableLineChart = "false";
			$scope.enableStepLineChart = "false";
			$scope.enableBarChart = "false";
		} else if (arg === 'Stacked') {
			$scope.enableAreaChart = "false";
			$scope.enablePieChart = "false";
			$scope.enableStackedBarChart = "true";
			$scope.enableRadarChart = "false";
			$scope.enableLineChart = "false";
			$scope.enableStepLineChart = "false";
			$scope.enableBarChart = "false";
		} else if (arg === 'Radar') {
			$scope.enableAreaChart = "false";
			$scope.enablePieChart = "false";
			$scope.enableStackedBarChart = "false";
			$scope.enableRadarChart = "true";
			$scope.enableLineChart = "false";
			$scope.enableStepLineChart = "false";
			$scope.enableBarChart = "false";
		} else if (arg === 'Line') {
			$scope.enableAreaChart = "false";
			$scope.enablePieChart = "false";
			$scope.enableStackedBarChart = "false";
			$scope.enableRadarChart = "false";
			$scope.enableLineChart = "true";
			$scope.enableStepLineChart = "false";
			$scope.enableBarChart = "false";
		} else if (arg === 'StepLine') {
			$scope.enableAreaChart = "false";
			$scope.enablePieChart = "false";
			$scope.enableStackedBarChart = "false";
			$scope.enableRadarChart = "false";
			$scope.enableLineChart = "false";
			$scope.enableStepLineChart = "true";
			$scope.enableBarChart = "false";
		} else if (arg === 'Bar') {
			$scope.enableAreaChart = "false";
			$scope.enablePieChart = "false";
			$scope.enableStackedBarChart = "false";
			$scope.enableRadarChart = "false";
			$scope.enableLineChart = "false";
			$scope.enableStepLineChart = "false";
			$scope.enableBarChart = "true";
		}
	};
	
	$scope.test = function() {
		$scope.child.chartOptions.dataSource.reload();
	};
	
	init();
});

// 영역형 차트 컨트롤러
app.controller('chartController', function ($scope) {
	var parentScope = $scope.$parent;
	parentScope.child = $scope;
	
	var customStore = new DevExpress.data.CustomStore({
	    load: function (loadOptions) {
	        var d = $.Deferred();
	        $('#dxChart').dxChart('instance').showLoadingIndicator();
	        $.getJSON('web/tbsuplymst/').done(function (data) {
	            d.resolve(data);
	        });
	        return d.promise();
	    }
	});
	
	var dataSource = new DevExpress.data.DataSource({
	    store: customStore
	});
	
    $scope.chartOptions = {
        dataSource: dataSource,
        commonSeriesSettings: {
            argumentField: 'ARGUMENT_FIELD',
            type: 'fullStackedSplineArea'
        },
        animate: true,
        series: [
            { name: '공공임대', valueField: '10' },
            { name: '국민임대', valueField: '20' },
            { name: '영구임대', valueField: '30' },
            { name: '기존주택전세임대', valueField: '40' },
            { name: '기존주택매입임대', valueField: '50'},
            { name: '매입아파트', valueField: '60'},
            { name: '신축다세대', valueField: '70'},
            { name: '미정의', valueField: '99'},
        ],
        bindingOption: {
        	dataSource: 'dataSource'
        }    		
    };
    
});

